return function m(
number,
one,
two,
five,
includeNumber = true)
{
  const text = [five, one, two, two, two, five][
  number % 100 > 10 && number % 100 < 20 ? 0 : Math.min(number % 10, 5)];

  return includeNumber ? `${number} ${text}` : text;
}

